"use client";

import React, { useRef } from "react";
import Image from "next/image";
import { Phone, ArrowRight } from "lucide-react";
import { gsap, useGSAP } from "@/lib/gsap-animations";
import MagneticButton from "./MagneticButton";
import SplitTextReveal from "./SplitTextReveal";

const Hero = () => {
    const containerRef = useRef<HTMLDivElement>(null);
    const imageRef = useRef<HTMLDivElement>(null);
    const textRef = useRef<HTMLParagraphElement>(null);
    const actionsRef = useRef<HTMLDivElement>(null);
    const overlayRef = useRef<HTMLDivElement>(null);

    useGSAP(() => {
        const tl = gsap.timeline({ defaults: { ease: "power4.out" } });

        // Initial state for non-split elements
        gsap.set([textRef.current, actionsRef.current], {
            opacity: 0,
            y: 30
        });
        gsap.set(imageRef.current, { scale: 1.2, filter: "blur(10px)" });
        gsap.set(overlayRef.current, { opacity: 0 });

        // Entrance Animation Sequence
        tl.to(overlayRef.current, { opacity: 1, duration: 1.5 })
            .to(imageRef.current, { scale: 1, filter: "blur(0px)", duration: 2 }, 0)
            .to(textRef.current, { opacity: 1, y: 0, duration: 1 }, 1)
            .to(actionsRef.current, { opacity: 1, y: 0, duration: 1 }, 1.2);

        // Parallax Scroll Effect
        gsap.to(imageRef.current, {
            yPercent: 20,
            ease: "none",
            scrollTrigger: {
                trigger: containerRef.current,
                start: "top top",
                end: "bottom top",
                scrub: true,
            },
        });

        // Content subtle float up on scroll
        gsap.to([textRef.current, actionsRef.current], {
            y: -30,
            stagger: 0.05,
            ease: "none",
            scrollTrigger: {
                trigger: containerRef.current,
                start: "top top",
                end: "bottom top",
                scrub: true,
            },
        });
    }, { scope: containerRef });

    return (
        <section ref={containerRef} className="relative h-screen min-h-[750px] w-full overflow-hidden flex items-center">
            {/* Background Layer */}
            <div className="absolute inset-0 z-0">
                <div ref={imageRef} className="relative h-[120%] w-full -top-[10%]">
                    <Image
                        src="/images/b_Image_de_fond_profes.jpeg"
                        alt="Hero Background"
                        fill
                        className="object-cover object-center"
                        priority
                    />
                </div>
                <div ref={overlayRef} className="absolute inset-0 bg-gradient-to-r from-primary/95 via-primary/80 to-transparent z-10" />
            </div>

            <div className="container mx-auto px-4 relative z-20">
                <div className="max-w-4xl">
                    <SplitTextReveal
                        className="text-secondary font-bold tracking-[0.4em] uppercase mb-8 block drop-shadow-sm text-sm md:text-base"
                        type="chars"
                        delay={0.5}
                    >
                        L'excellence à votre service
                    </SplitTextReveal>

                    <SplitTextReveal
                        className="text-6xl md:text-9xl font-extrabold text-white leading-[0.95] mb-10 tracking-tighter"
                        type="chars"
                        delay={0.7}
                    >
                        Propulsez Votre Vision
                    </SplitTextReveal>

                    <p
                        ref={textRef}
                        className="text-white/70 text-xl md:text-2xl mb-14 max-w-2xl leading-relaxed font-light"
                    >
                        TAW 10 n&apos;est pas qu&apos;une simple adresse. C&apos;est l&apos;écosystème qui transforme vos ambitions en réalité concrète au cœur de Marrakech.
                    </p>

                    <div
                        ref={actionsRef}
                        className="flex flex-wrap gap-10 items-center"
                    >
                        <MagneticButton>
                            <a
                                href="#contact"
                                className="bg-secondary text-primary px-12 py-6 rounded-full font-bold text-xl flex items-center gap-4 hover:bg-white transition-all shadow-2xl group overflow-hidden relative"
                            >
                                <span className="relative z-10">Commencer Maintenant</span>
                                <ArrowRight size={24} className="group-hover:translate-x-2 transition-transform relative z-10" />
                            </a>
                        </MagneticButton>

                        <div className="flex flex-col gap-2 group cursor-pointer">
                            <a
                                href="tel:+212524308038"
                                className="text-white group-hover:text-secondary transition-colors font-bold text-2xl flex items-center gap-4 decoration-secondary/30 underline-offset-8 underline"
                            >
                                <div className="bg-white/10 p-2 rounded-full group-hover:bg-secondary group-hover:text-primary transition-all">
                                    <Phone size={22} />
                                </div>
                                +212 52430-8038
                            </a>
                            <span className="text-white/40 text-xs ml-14 italic tracking-widest uppercase">Consultation 24/7</span>
                        </div>
                    </div>
                </div>
            </div>

            {/* Decorative Elements */}
            <div className="absolute top-1/2 -right-48 w-96 h-96 bg-secondary/10 rounded-full blur-[150px] z-10 animate-pulse" />
            <div className="absolute -bottom-24 -left-24 w-80 h-80 bg-blue-500/10 rounded-full blur-[150px] z-10" />

            {/* Scroll Indicator */}
            <div className="absolute bottom-12 left-1/2 -translate-x-1/2 z-30 flex flex-col items-center gap-4 opacity-40">
                <div className="w-[1px] h-20 bg-gradient-to-b from-white to-transparent" />
                <span className="text-white text-[10px] uppercase tracking-[0.5em] font-bold vertical-text">Scroll</span>
            </div>
        </section>
    );
};

export default Hero;

"use client";

import React, { useState, useEffect } from "react";
import Image from "next/image";
import Link from "next/link";
import { Menu, X, Globe, Phone, Clock } from "lucide-react";
import { cn } from "@/lib/utils";

const Navbar = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [scrolled, setScrolled] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            setScrolled(window.scrollY > 20);
        };
        window.addEventListener("scroll", handleScroll);
        return () => window.removeEventListener("scroll", handleScroll);
    }, []);

    const navLinks = [
        { name: "Accueil", href: "#" },
        { name: "Services", href: "#services" },
        { name: "Pourquoi Nous", href: "#pourquoi" },
        { name: "Tarifs", href: "#tarifs" },
        { name: "Contact", href: "#contact" },
    ];

    return (
        <>
            {/* Top Bar */}
            <div className="hidden lg:block bg-primary text-white py-2">
                <div className="container mx-auto px-4 flex justify-between items-center text-sm">
                    <div className="flex items-center gap-4">
                        <span className="flex items-center gap-1">
                            <Clock size={14} className="text-secondary" />
                            Lundi-Samedi 09:00 - 20:00
                        </span>
                    </div>
                    <div className="flex items-center gap-6">
                        <a href="tel:+212524308038" className="flex items-center gap-1 hover:text-secondary transition-colors">
                            <Phone size={14} className="text-secondary" />
                            +212 52430-8038
                        </a>
                        <div className="flex items-center gap-2">
                            <Link href="?lang=fr" className="hover:text-secondary transition-colors font-bold">FR</Link>
                            <span className="text-white/30">|</span>
                            <Link href="?lang=ar" className="hover:text-secondary transition-colors">AR</Link>
                            <span className="text-white/30">|</span>
                            <Link href="?lang=en" className="hover:text-secondary transition-colors">EN</Link>
                        </div>
                    </div>
                </div>
            </div>

            {/* Main Header */}
            <header
                className={cn(
                    "sticky top-0 z-50 transition-all duration-300 w-full",
                    scrolled ? "bg-white/90 backdrop-blur-md shadow-lg py-3" : "bg-white py-5"
                )}
            >
                <div className="container mx-auto px-4">
                    <nav className="flex items-center justify-between">
                        {/* Logo */}
                        <Link href="/" className="relative h-12 w-32">
                            <Image
                                src="/images/logo/IMG_1507.png"
                                alt="TAW10 Logo"
                                fill
                                className="object-contain"
                                priority
                            />
                        </Link>

                        {/* Desktop Navigation */}
                        <ul className="hidden lg:flex items-center gap-8">
                            {navLinks.map((link) => (
                                <li key={link.name}>
                                    <Link
                                        href={link.href}
                                        className="text-primary font-medium hover:text-secondary transition-colors relative group"
                                    >
                                        {link.name}
                                        <span className="absolute -bottom-1 left-0 w-0 h-0.5 bg-secondary transition-all group-hover:w-full" />
                                    </Link>
                                </li>
                            ))}
                        </ul>

                        {/* Actions */}
                        <div className="hidden lg:flex items-center gap-4">
                            <Link
                                href="#contact"
                                className="bg-primary text-white px-6 py-2.5 rounded-full font-bold hover:bg-primary-light transition-all shadow-md hover:shadow-xl transform hover:-translate-y-0.5"
                            >
                                Consultation
                            </Link>
                        </div>

                        {/* Mobile Menu Button */}
                        <button
                            className="lg:hidden text-primary p-2"
                            onClick={() => setIsOpen(!isOpen)}
                        >
                            {isOpen ? <X size={28} /> : <Menu size={28} />}
                        </button>
                    </nav>
                </div>

                {/* Mobile menu */}
                <div
                    className={cn(
                        "lg:hidden absolute top-full left-0 w-full bg-white border-t border-gray-100 shadow-xl transition-all duration-300 ease-in-out overflow-hidden",
                        isOpen ? "max-height-[500px] opacity-100 py-6" : "max-height-0 opacity-0"
                    )}
                >
                    <ul className="flex flex-col items-center gap-6">
                        {navLinks.map((link) => (
                            <li key={link.name}>
                                <Link
                                    href={link.href}
                                    className="text-xl text-primary font-medium"
                                    onClick={() => setIsOpen(false)}
                                >
                                    {link.name}
                                </Link>
                            </li>
                        ))}
                        <Link
                            href="#contact"
                            className="bg-primary text-white px-8 py-3 rounded-full font-bold"
                            onClick={() => setIsOpen(false)}
                        >
                            Consultation
                        </Link>
                        <div className="flex items-center gap-4 pt-4 border-t w-full justify-center">
                            <Link href="?lang=fr" className="text-secondary font-bold">FR</Link>
                            <Link href="?lang=ar" className="text-primary">AR</Link>
                            <Link href="?lang=en" className="text-primary">EN</Link>
                        </div>
                    </ul>
                </div>
            </header>
        </>
    );
};

export default Navbar;

